RShape.prototype.getSelectMarkPC = function(type, point) {
	var ret = true;
	switch(type) {
		case Shape.HIT_TEST_TL: {
			point.x = 0;
			point.y = 0;
			break;
		}
		case Shape.HIT_TEST_TM: {
			point.x = this.w >> 1;
			point.y = 0;
			break;
		}
		case Shape.HIT_TEST_TR: {
			point.x = this.w;
			point.y = 0;
			break;
		}
		case Shape.HIT_TEST_ML: {
			point.x = 0;
			point.y = this.h >> 1;
			break;
		}
		case Shape.HIT_TEST_MR: {
			point.x = this.w;
			point.y = this.h >> 1;
			break;
		}
		case Shape.HIT_TEST_BL: {
			point.x = 0;
			point.y = this.h;
			break;
		}
		case Shape.HIT_TEST_BM: {
			point.x = this.w >> 1;
			point.y = this.h;
			break;
		}
		case Shape.HIT_TEST_BR: {
			point.x = this.w;
			point.y = this.h;
			break;
		}
		case Shape.HIT_TEST_ROTATION: {
			point.x = this.w >> 1;
			point.y = this.h - 100;
			break;
		}
		default: {
			ret = this.getMoreSelectMark(type, point);
		}
	}
	
	return ret;
}

