UIScene.prototype.beforePaintChildren = function(canvas) {
	var x = 0;
	var y = 0;
	var arr = [];
	var color = null;

	if(!this.debug && this.mode != Shape.MODE_EDITING) {
		return;
	}

	for(var i = 0; i < this.children.length; i++) {
		var iter = this.children[i];
		if(!iter.isUIFootprint) {
			continue;
		}

		arr.push(iter);
	}

	stableSortMerge(arr, function(a, b) {
		if(a.name > b.name) {
			return 1;
		}

		if(a.name < b.name) {
			return -1;
		}
		else {
			return 0;
		}
	});

	canvas.lineCap = "round";
	canvas.lineJoin = "round";
	canvas.lineWidth = 5;
	for(var i = 0; i <arr.length; i++) {
		var iter = arr[i];
		x = iter.x + (iter.w >> 1);
		y = iter.y + (iter.h >> 1);

		if(i === 0 || iter.name != arr[i-1].name) {
			if(i != 0) {
				canvas.strokeStyle = color;
				canvas.stroke();
				canvas.beginPath();
			}

			canvas.moveTo(x, y);
			color = iter.style.fillColor;
		}
		else {
			canvas.lineTo(x, y);
		}
	}
	canvas.strokeStyle = color;
	canvas.stroke();

	return;
}

